import React, { useState, useEffect } from "react";
import axios from "axios";
import { Container, Form, Button, Table, Alert, Spinner } from "react-bootstrap";

const Dashboard = () => {
  const [patients, setPatients] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [message, setMessage] = useState("");
  const [newPatient, setNewPatient] = useState({
    name: "",
    age: "",
    gender: "",
    address: "",
    contact: "",
    wardNumber: "",
    status: "Admitted"
  });

  useEffect(() => {
    fetchPatients();
  }, []);

  const fetchPatients = async () => {
    setLoading(true);
    setError(null);
    try {
      const response = await axios.get("http://localhost:5000/api/patients", {
        headers: { Authorization: `Bearer YOUR_ADMIN_TOKEN` },
      });
      if (Array.isArray(response.data)) {
        setPatients(response.data);
      } else {
        setPatients([]);
      }
    } catch (error) {
      setError("Failed to fetch patients.");
    }
    setLoading(false);
  };

  const registerPatient = async (e) => {
    e.preventDefault();
    setMessage("");
    setError(null);
    try {
      await axios.post("http://localhost:5000/api/patients/register", newPatient, {
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer YOUR_ADMIN_TOKEN`,
        },
      });
      setMessage("Patient registered successfully.");
      setNewPatient({ name: "", age: "", gender: "", address: "", contact: "", wardNumber: "", status: "Admitted" });
      fetchPatients();
    } catch (error) {
      setError("Failed to register patient.");
    }
  };

  return (
    <Container>
      <h2 className="my-4">Admin Dashboard</h2>
      {message && <Alert variant="success">{message}</Alert>}
      {error && <Alert variant="danger">{error}</Alert>}

      <h4>Register Patient</h4>
      <Form onSubmit={registerPatient} className="mb-4">
        <Form.Group>
          <Form.Label>Name</Form.Label>
          <Form.Control type="text" value={newPatient.name} onChange={(e) => setNewPatient({ ...newPatient, name: e.target.value })} required />
        </Form.Group>
        <Form.Group>
          <Form.Label>Age</Form.Label>
          <Form.Control type="number" value={newPatient.age} onChange={(e) => setNewPatient({ ...newPatient, age: e.target.value })} required />
        </Form.Group>
        <Form.Group>
          <Form.Label>Gender</Form.Label>
          <Form.Select value={newPatient.gender} onChange={(e) => setNewPatient({ ...newPatient, gender: e.target.value })} required>
            <option value="">Select</option>
            <option value="Male">Male</option>
            <option value="Female">Female</option>
          </Form.Select>
        </Form.Group>
        <Form.Group>
          <Form.Label>Address</Form.Label>
          <Form.Control type="text" value={newPatient.address} onChange={(e) => setNewPatient({ ...newPatient, address: e.target.value })} required />
        </Form.Group>
        <Form.Group>
          <Form.Label>Contact</Form.Label>
          <Form.Control type="text" value={newPatient.contact} onChange={(e) => setNewPatient({ ...newPatient, contact: e.target.value })} required />
        </Form.Group>
        <Form.Group>
          <Form.Label>Ward Number</Form.Label>
          <Form.Control type="text" value={newPatient.wardNumber} onChange={(e) => setNewPatient({ ...newPatient, wardNumber: e.target.value })} required />
        </Form.Group>
        <Form.Group>
          <Form.Label>Status</Form.Label>
          <Form.Select value={newPatient.status} onChange={(e) => setNewPatient({ ...newPatient, status: e.target.value })} required>
            <option value="Admitted">Admitted</option>
            <option value="Discharged">Discharged</option>
          </Form.Select>
        </Form.Group>
        <Button type="submit" className="mt-3">Register</Button>
      </Form>

      <h4 className="mt-4">All Patients</h4>
      {loading ? (
        <Spinner animation="border" />
      ) : (
        <Table striped bordered hover className="mt-3">
          <thead>
            <tr>
              <th>Name</th>
              <th>Age</th>
              <th>Gender</th>
              <th>Contact</th>
              <th>Address</th>
              <th>Ward Number</th>
              <th>Status</th>
              <th>Registered Date</th>
            </tr>
          </thead>
          <tbody>
            {patients.length > 0 ? (
              patients.map((patient) => (
                <tr key={patient._id}>
                  <td>{patient.name}</td>
                  <td>{patient.age}</td>
                  <td>{patient.gender}</td>
                  <td>{patient.contact}</td>
                  <td>{patient.address}</td>
                  <td>{patient.wardNumber}</td>
                  <td>{patient.status}</td>
                  <td>{new Date(patient.createdAt).toLocaleString()}</td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="8" className="text-center">No patients found</td>
              </tr>
            )}
          </tbody>
        </Table>
      )}
    </Container>
  );
};

export default Dashboard;
