import React, { useEffect, useState } from "react";
import axios from "axios";
import { useAuth } from "../hooks/useAuth"; // Ensure this is correctly set up
import Table from "../components/Table"; // Ensure you have a reusable Table component

const Dashboard = () => {
  const { token } = useAuth(); // Get token from AuthContext
  const [patients, setPatients] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (!token) return; // Ensure token is available before making requests

    const fetchPatients = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/patients", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setPatients(response.data);
      } catch (err) {
        setError("Failed to fetch patients");
      } finally {
        setLoading(false);
      }
    };

    fetchPatients();
  }, [token]);

  return (
    <div className="container mt-4">
      <h2>Dashboard - Patient List</h2>

      {loading && <p>Loading patients...</p>}
      {error && <p className="text-danger">{error}</p>}

      {!loading && !error && patients.length > 0 ? (
        <Table
          data={patients}
          columns={["name", "age", "gender", "contact", "address", "medicalHistory"]}
        />
      ) : (
        <p>No patients found.</p>
      )}
    </div>
  );
};

export default Dashboard;
