import axios from "axios";
import API_BASE_URL from "../config";

// Register User
export const registerUser = async (userData) => {
  try {
    const response = await axios.post(`${API_BASE_URL}/auth/register`, userData);
    return response.data; // Returns success message or user data
  } catch (error) {
    throw error.response ? error.response.data : new Error("Registration failed");
  }
};

// Login User
export const loginUser = async (credentials) => {
  try {
    const response = await axios.post(`${API_BASE_URL}/auth/login`, credentials);
    return response.data; // Returns token and user data
  } catch (error) {
    throw error.response ? error.response.data : new Error("Login failed");
  }
};

// Get User Profile
export const getUserProfile = async () => {
  try {
    const token = localStorage.getItem("token");
    if (!token) throw new Error("Unauthorized");

    const response = await axios.get(`${API_BASE_URL}/auth/profile`, {
      headers: { Authorization: `Bearer ${token}` },
    });

    return response.data; // Returns user profile data
  } catch (error) {
    throw error.response ? error.response.data : new Error("Failed to fetch profile");
  }
};

// Logout User
export const logoutUser = () => {
  localStorage.removeItem("token");
};
