import React, { useState, useEffect } from "react";
import axios from "axios";
import { Container, Form, Button, Table, Alert, Spinner, Pagination, Card } from "react-bootstrap";
import { useNavigate } from "react-router-dom";

const Dashboard = () => {
  const [patients, setPatients] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [message, setMessage] = useState("");
  const [currentPage, setCurrentPage] = useState(1);
  const [user, setUser] = useState(null);
  const patientsPerPage = 5;
  const navigate = useNavigate();

  const [newPatient, setNewPatient] = useState({
    name: "",
    age: "",
    gender: "",
    address: "",
    contact: "",
    wardNumber: "",
    bedNumber: "",
    status: "Admitted",
  });

  useEffect(() => {
    fetchUserProfile();
    fetchPatients();
  }, []);

  const fetchUserProfile = async () => {
    setLoading(true);
    try {
      const token = localStorage.getItem("token");
      const response = await axios.get("http://localhost:5000/api/auth/profile", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setUser(response.data);
    } catch (error) {
      setError("Failed to fetch user profile.");
    }
    setLoading(false);
  };

  const fetchPatients = async () => {
    setLoading(true);
    setError(null);
    try {
      const token = localStorage.getItem("token");
      const response = await axios.get("http://localhost:5000/api/patients", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setPatients(Array.isArray(response.data) ? response.data : []);
    } catch (error) {
      setError("Failed to fetch patients.");
    }
    setLoading(false);
  };

  const registerPatient = async (e) => {
    e.preventDefault();
    setMessage("");
    setError(null);
    try {
      const token = localStorage.getItem("token");
      await axios.post("http://localhost:5000/api/patients/register", newPatient, {
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
      });
      setMessage("Patient registered successfully.");
      setNewPatient({ name: "", age: "", gender: "", address: "", contact: "", wardNumber: "", bedNumber: "", status: "Admitted" });
      fetchPatients();
    } catch (error) {
      setError("Failed to register patient.");
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/");
  };

  const indexOfLastPatient = currentPage * patientsPerPage;
  const indexOfFirstPatient = indexOfLastPatient - patientsPerPage;
  const currentPatients = patients.slice(indexOfFirstPatient, indexOfLastPatient);
  const totalPages = Math.ceil(patients.length / patientsPerPage);

  return (
    <Container className="mt-4">
      <div className="d-flex justify-content-between align-items-center">
        <h2 className="text-primary">Admin Dashboard</h2>
        <Button variant="danger" onClick={handleLogout}>Logout</Button>
      </div>

      {user && (
        <Card className="shadow mt-3 p-3">
          <h5>Welcome, {user.name} ({user.role})</h5>
          <p>Email: {user.email}</p>
        </Card>
      )}

      {message && <Alert variant="success" className="mt-3">{message}</Alert>}
      {error && <Alert variant="danger" className="mt-3">{error}</Alert>}

      {/* Register Patient Card */}
      <Card className="shadow mt-4">
        <Card.Header className="bg-primary text-white">Register New Patient</Card.Header>
        <Card.Body>
          <Form onSubmit={registerPatient}>
            <div className="row">
              <Form.Group className="mb-3 col-md-6">
                <Form.Label>Name</Form.Label>
                <Form.Control type="text" value={newPatient.name} onChange={(e) => setNewPatient({ ...newPatient, name: e.target.value })} required />
              </Form.Group>
              <Form.Group className="mb-3 col-md-6">
                <Form.Label>Age</Form.Label>
                <Form.Control type="number" value={newPatient.age} onChange={(e) => setNewPatient({ ...newPatient, age: e.target.value })} required />
              </Form.Group>
              <Form.Group className="mb-3 col-md-6">
                <Form.Label>Gender</Form.Label>
                <Form.Control as="select" value={newPatient.gender} onChange={(e) => setNewPatient({ ...newPatient, gender: e.target.value })} required>
                  <option value="">Select</option>
                  <option value="Male">Male</option>
                  <option value="Female">Female</option>
                </Form.Control>
              </Form.Group>
              <Form.Group className="mb-3 col-md-6">
                <Form.Label>Ward Number</Form.Label>
                <Form.Control type="text" value={newPatient.wardNumber} onChange={(e) => setNewPatient({ ...newPatient, wardNumber: e.target.value })} required />
              </Form.Group>
            </div>
            <Button type="submit" className="mt-2 w-100 btn-success">Register Patient</Button>
          </Form>
        </Card.Body>
      </Card>

      {/* Patients Table Card */}
      <Card className="shadow mt-4">
        <Card.Header className="bg-dark text-white">All Patients</Card.Header>
        <Card.Body>
          {loading ? (
            <div className="text-center">
              <Spinner animation="border" />
            </div>
          ) : (
            <>
              <Table striped bordered hover responsive className="mt-3">
                <thead className="table-dark">
                  <tr>
                    <th>#</th>
                    <th>Name</th>
                    <th>Age</th>
                    <th>Gender</th>
                    <th>Contact</th>
                    <th>Address</th>
                    <th>Ward</th>
                    <th>Bed</th>
                    <th>Status</th>
                    <th>Registered</th>
                  </tr>
                </thead>
                <tbody>
                  {currentPatients.map((patient, index) => (
                    <tr key={patient._id}>
                      <td>{indexOfFirstPatient + index + 1}</td>
                      <td>{patient.name}</td>
                      <td>{patient.age}</td>
                      <td>{patient.gender}</td>
                      <td>{patient.contact}</td>
                      <td>{patient.address}</td>
                      <td>{patient.wardNumber || "N/A"}</td>
                      <td>{patient.bedNumber || "N/A"}</td>
                      <td>{patient.status}</td>
                      <td>{new Date(patient.createdAt).toLocaleString()}</td>
                    </tr>
                  ))}
                </tbody>
              </Table>
            </>
          )}
        </Card.Body>
      </Card>
    </Container>
  );
};

export default Dashboard;
