import React, { useContext, useState } from "react";
import { PatientContext } from "../context/PatientContext";
import { registerPatient } from "../services/patientService";
import { useAuth } from "../hooks/useAuth";
import { Container, Form, Button, Table, Alert } from "react-bootstrap";

const Dashboard = () => {
  const { patients, fetchPatients } = useContext(PatientContext);
  const { token } = useAuth();
  const [newPatient, setNewPatient] = useState({
    name: "",
    age: "",
    gender: "",
    address: "",
    contact: "",
    wardNumber: "",
    status: "",
  });
  const [message, setMessage] = useState("");

  const handleRegister = async (e) => {
    e.preventDefault();
    try {
      await registerPatient(newPatient, token);
      setMessage("Patient registered successfully!");
      fetchPatients(); // Refresh list
    } catch (error) {
      setMessage("Error registering patient");
    }
  };

  return (
    <Container>
      <h2>Admin Dashboard</h2>
      {message && <Alert variant="success">{message}</Alert>}

      <h4>Register Patient</h4>
      <Form onSubmit={handleRegister}>
        <Form.Group>
          <Form.Label>Name</Form.Label>
          <Form.Control
            type="text"
            onChange={(e) =>
              setNewPatient({ ...newPatient, name: e.target.value })
            }
            required
          />
        </Form.Group>
        <Button type="submit" className="mt-2">Register</Button>
      </Form>

      <h4>All Patients</h4>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>Name</th>
            <th>Age</th>
            <th>Gender</th>
            <th>Contact</th>
            <th>Address</th>
            <th>Ward</th>
            <th>Status</th>
          </tr>
        </thead>
        <tbody>
          {patients.map((patient) => (
            <tr key={patient._id}>
              <td>{patient.name}</td>
              <td>{patient.age}</td>
              <td>{patient.gender}</td>
              <td>{patient.contact}</td>
              <td>{patient.address}</td>
              <td>{patient.wardNumber}</td>
              <td>{patient.status}</td>
            </tr>
          ))}
        </tbody>
      </Table>
    </Container>
  );
};

export default Dashboard;
